import React, { useState, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import axios from 'axios';
import { v4 as uuidv4 } from 'uuid';
import Popup from '../src/components/Popup';

function App() {

  const itemsFromBackend = [
    { id: uuidv4(), content: 'First task'},
    { id: uuidv4(), content: 'Second task'},
    { id: uuidv4(), content: 'Struct app'},
    { id: uuidv4(), content: 'Hello, testing card'}
  ]

  const columnsFromBackend = 
    {
        [uuidv4()]: {
            name: 'ideas',
            items: itemsFromBackend
        },
        [uuidv4()]: {
            name: 'this week',
            items: []
        },
        [uuidv4()]: {
            name: 'in progress',
            items: []
        },
        [uuidv4()]: {
            name: 'pending approval',
            items: []
        },
        [uuidv4()]: {
            name: 'approved',
            items: []
        },
        [uuidv4()]: {
            name: 'posted',
            items: [] 
        }
    };

  const [ columns, setColumns ] = useState(columnsFromBackend);

  const [ cardPopup, setCardPopup ] = useState(false);
  const [ cardTitle, setCardTitle ] = useState("");
  const [ cardContent, setCardContent ] = useState("");
  const [ cardPlatform, setCardPlatform ] = useState("");

  const onDragEnd = (result, columns, setColumns) => {
    if (!result.destination) return;
    const { source, destination } = result;
    if (source.droppableId !== destination.droppableId) {
      const sourceColumn = columns[source.droppableId];
      const destColumn = columns[destination.droppableId]
      const sourceItems = [...sourceColumn.items];
      const destItems = [...destColumn.items];
      const [ removed ] = sourceItems.splice(source.index, 1);
      destItems.splice(destination.index, 0, removed);
      setColumns({
        ...columns,
        [source.droppableId]: {
          ...sourceColumn,
          items: sourceItems
        },
        [destination.droppableId]: {
          ...destColumn,
          items: destItems
        }
      })
    } else {
      const column = columns[source.droppableId];
      const copiedItems = [...column.items];
      const [ removed ] = copiedItems.splice(source.index, 1);
      copiedItems.splice(destination.index, 0, removed);
      setColumns({
        ...columns,
        [source.droppableId]: {
          ...column,
          items: copiedItems
        }
      });
    }
  };

  const addCard = (e) => {
    e.preventDefault();
    axios
      .post('http://localhost:3003/cards', {
        title: cardTitle,
        content: cardContent,
        platform: cardPlatform
      })
      .then((res) => {
        if (!alert('Created successful')) {
          window.location.reload();
        };
      });
  }

  return (
    <div>
     <h1 style={{ marginTop: '40px',textAlign: 'center', color: '#242835', fontSize: 50, fontWeight: 800}}>content workflow</h1>
      <div style={{ display: 'flex', justifyContent: 'center', height: '100%' }}>
        <DragDropContext onDragEnd={result => onDragEnd(result, columns, setColumns)}>
          {Object.entries(columns).map(([id, column]) => {
            return (
              <div style={{display: 'flex', flexDirection: 'column', alignItems: 'center'}}>
                <h2 style={{ color: '#059A9D', fontSize: 22}}>{column.name}</h2>
                <div style={{ margin: 8 }}>
                  <Droppable droppableId={id} key={id}>
                    {(provided, snapshot) => {
                      return (
                        <div 
                          {...provided.droppableProps}
                          ref={provided.innerRef}
                          style={{
                            background: snapshot.isDraggingOver ? 'lightblue' : 'lightgrey',
                            padding: 10,
                            width: 230,
                            minHeight: 50,
                            borderRadius: 10
                          }}
                        >
                          {column.items.map((item, index) => {
                            return (
                              <div>
                                <Draggable key={item.id} draggableId={item.id} index={index}>
                                  {(provided, snapshot) => {
                                    return (
                                      <div ref={provided.innerRef}
                                        {...provided.draggableProps}
                                        {...provided.dragHandleProps}
                                          style={{
                                            userSelect: 'none',
                                            padding: 16,
                                            margin: '0 0 8px 0',
                                            maxHeight: '50px',
                                            backgroundColor: snapshot.isDragging ? '#263b4a' : '#456c86',
                                            color: 'white',
                                            fontWeight: 'bold',
                                            borderRadius: '10px',
                                            ...provided.draggableProps.style
                                          }}                                      
                                        >
                                          <div>
                                            {item.content}
                                          </div>
                                      </div>
                                    )
                                  }}
                                </Draggable>
                              </div>
                            )
                          })}
                          {provided.placeholder}
                          <p style={{ textAlign: 'center', cursor: 'pointer' }} onClick={() => {setCardPopup(true)}} >➕ Add new card</p>
                      </div>
                    )
                  }}
                </Droppable>
              </div>
            </div>
          )})}
          <button> Add new list </button>
        </DragDropContext>
      </div>

      <Popup trigger={cardPopup} setTrigger={setCardPopup}>
        <form onSubmit={addCard}>
          <label>Title</label>
          <input 
            autoComplete='off'
            type='text'
            value={cardTitle}
            onChange={(e) => {
              setCardTitle(e.target.value)
            }}
          />
          <br />
          <label>Content</label>
          <input 
            autoComplete='off'
            type='text'
            value={cardContent}
            onChange={(e) => {
              setCardContent(e.target.value)
            }}
          />
          <br />
          <label>Platform</label>
          <input 
            autoComplete='off'
            type='text'
            value={cardPlatform}
            onChange={(e) => {
              setCardPlatform(e.target.value)
            }}
          />
          <br />
          <button type="submit" > Add New Card </button>
        </form>
      </Popup>

    </div>
  );
}

export default App;
